    private void jComboBox1PerawatanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1PerawatanActionPerformed
        // TODO add your handling code here:
        int index  = jComboBox1Perawatan.getSelectedIndex();
        switch (index) {
            case 0:
                Harga.setText("");
                break;
            case 1:
                Harga.setText("50000");
                break;
            default:
                Harga.setText("75000");
                break;
        }
        
    }//GEN-LAST:event_jComboBox1PerawatanActionPerformed

