    private void cleatButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cleatButtonActionPerformed
        // TODO add your handling code here:
        jRadioButtonMember.setSelected(false);
        jRadioButtonNonMember.setSelected(false);
        jCheckBoxMessage.setSelected(false);
        jCheckBoxSpa.setSelected(false);
        jTextFieldDiskon.setText("");
        TotalBayar.setText("");
        Harga.setText("");
        jComboBox1Perawatan.setSelectedIndex(0);
    }//GEN-LAST:event_cleatButtonActionPerformed

